        var filterStr = app.date().subtract('days', this.settings.get('filter')).format('YYYY-MM-DD');

        var tab = this.tabs[index],
            filter = {},
            filters = [];

        filter[tab.filter_applied_to] = {$gte: filterStr};

        filters.push(filter);

