    /**
     * @dataProvider dataProviderMigrateToOpportunities
     */
    public function testMigrateToOpportunities($id, $name, $content, $validHash)
    {
        $this->db->addQuerySpy(
            'select_saved_reports',
            '/saved_reports.module = \'Opportunities\' AND saved_reports.content LIKE \'%/',
            array(
                array(
                    'id' => $id,
                    'name' => $name,
                    'content' => $content
                )
            )
        );

        $this->db->addQuerySpy('update_saved_reports', '/UPDATE saved_reports/');

        $converter = new OpportunityReports();
        $reports = $converter->migrateToOpportunities();
        $this->assertEquals($validHash, md5($reports[0]));
    }
}
